mapper.select:
SELECT article.id, article.title, article.cover, article.summary, iu.id u_id, iu.nick_name, rau.status_id, article.created_time, article.updated_time, article.deleted, article.version FROM info_article article join relation_article_user rau on article.id = rau.article_id join info_user iu on rau.user_id = iu.id
rau.status_id = #{statusId}
and article.deleted = 0
ORDER BY article.updated_time
LIMIT #{offset},#{count};

SELECT article.id, article.title, article.cover, article.summary, article.content, iu.id u_id, iu.nick_name, rau.status_id, article.created_time, article.updated_time, article.deleted, article.version FROM info_article article join relation_article_user rau on article.id = rau.article_id join info_user iu on rau.user_id = iu.id
article.id = #{articleId}
and rau.status_id = #{statusId}
and article.deleted = 0
